    // Basically here to complain if one of the textures is not unique but proceeds anyways
    // This is very slow!!!! It needs tobe called only once
    public void ensureUnique() {
        HashMap<Biome, String> textureMapCopy;

        for(Map.Entry<Biome, String> texture : textureMap.entrySet()) {
            // Startover
            textureMapCopy = new HashMap<>(textureMap);

            // Remove this entry
            textureMapCopy.remove(texture.getKey());

            // Check to see if any other entries contain the same values
            if(textureMapCopy.containsValue(texture.getValue())) {
                plugin.getLogger().warning("Biome " + texture.getKey() + " has duplicate texture");
            }
        }

        // Startover
        textureMapCopy = new HashMap<>(textureMap);

        if(textureMapCopy.containsValue(defaultTexture)) {
            plugin.getLogger().warning("Biome default texture has duplicate texture in textureMap");
        }
    }

    public void ensureBiomesCovered() {
        for (Biome biome : Biome.values()) {
            if(!textureMap.containsKey(biome))
                plugin.getLogger().warning("Biome " + biome.getKey() + " does not have an associated texture");
        }
    }

    public String getTexture(Biome biome) {
        if(textureMap.containsKey(biome))
            return textureMap.get(biome);

        return defaultTexture;
    }

